import { Component } from '@angular/core';
import { Phone } from './Phone'

@Component({
  selector: 'app-phone-list',
  templateUrl: './phone-list.component.html',
  styleUrls: ['./phone-list.component.scss']
})
export class PhoneListComponent {

  phones: Phone[] = [
    {
      brand: "Samsung",
      model: "Galaxy S22",
      price: 800,
      stock: 10,
      image: "image",
      offer: false,
    },
    {
      brand: "Apple",
      model: "iPhone 14 Pro",
      price: 1000,
      stock: 1,
      image: "image",
      offer: false,
    },
    {
      brand: "Xiaomi",
      model: "Mi 10 Lite",
      price: 125,
      stock: 19,
      image: "image",
      offer: true,
    },
    {
      brand: "Google",
      model: "Pixel 7",
      price: 600,
      stock: 0,
      image: "image",
      offer: false,
    },
    {
      brand: "Motorola",
      model: "Moto G30",
      price: 299,
      stock: 24,
      image: "image",
      offer: false,
    },
  ]
}
