import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';
import { Phone } from './phone-list/Phone';

@Injectable({
  providedIn: 'root'
})
export class CartService {

  constructor() { }

  private _shoppingCart: Phone[] = [];
  shoppingCart: BehaviorSubject<Phone[]> = new BehaviorSubject(this._shoppingCart);

  addToCart(phone: Phone) {
    let item = this._shoppingCart.find((v1) => (v1.model == phone.model && v1.brand == phone.brand));
    if (phone.quantity > 0) {
      if (item) {
        item.quantity += phone.quantity;
      }
      else {
        this._shoppingCart.push({... phone});
      }
    }

    console.log(this._shoppingCart);
    this.shoppingCart.next(this._shoppingCart);
  }
}
