import { Component, OnInit } from '@angular/core';
import { FavouritesService } from '../favourites.service';
import { TechDataService } from '../tech-data.service';
import { Album } from './Album';

@Component({
  selector: 'app-album',
  templateUrl: './album.component.html',
  styleUrls: ['./album.component.scss']
})

export class AlbumComponent implements OnInit {
  param = 0;
  route: any;
  name: any;
  constructor(private favourites: FavouritesService, private dataService: TechDataService, private route: ActivatedRoute) {
  }

  ngOnInit(): void {
    this.getAll();
    this.route.queryParams.subscribe(params => {
      this.name = params['name'];
    });
  }

  getAll() {
    this.dataService.getAllAlbums().subscribe(albums => this.albums = albums);
  }

  albums: Album[] = [];

  addToCart(album: Album): void {
    this.favourites.addToCart2(album);
  }

  changeParam(number: number) {
    this.param = number;
  }
}
