import { Component } from '@angular/core';

@Component({
  selector: 'app-phone-list',
  templateUrl: './phone-list.component.html',
  styleUrls: ['./phone-list.component.scss']
})
export class PhoneListComponent {

  galaxyS22 = {
    "brand": "Samsung",
    "model": "Galaxy S22",
    "price": "$220.000",
    "stock": "1",
    "image": "image",
  }

  iPhone14Pro = {
    "brand": "Samsung",
    "model": "Galaxy S22",
    "price": "$220.000",
    "stock": "1",
    "image": "image",
  }

}
