import { Component } from '@angular/core';

@Component({
  selector: 'app-song',
  templateUrl: './song.component.html',
  styleUrls: ['./song.component.scss']
})
export class SongComponent {
  constructor(private cart: CartService, private dataService: TechDataService) {
  }

  ngOnInit(): void {
    this.dataService.getAll2().subscribe(albums => this.albums = albums);
  }

  albums: Album[] = [];

  addToCart(album: Album): void {
    this.cart.addToCart2(album);
  }
}
