import { Component } from '@angular/core';
import { Phone } from './Phone'

@Component({
  selector: 'app-phone-list',
  templateUrl: './phone-list.component.html',
  styleUrls: ['./phone-list.component.scss']
})
export class PhoneListComponent {


  mi10Lite: Phone = {

  }

  phones: Phone[] = [
    {
      brand: "Samsung",
      model: "Galaxy S22",
      price: 800,
      stock: 10,
      image: "image",
    },
    {
      brand: "Apple",
      model: "iPhone 14 Pro",
      price: 1000,
      stock: 1,
      image: "image",
    },
    {
      brand: "Xiaomi",
      model: "Mi 10 Lite",
      price: 125,
      stock: 19,
      image: "image",
    },
  ]
}
