import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable, tap } from 'rxjs';
import { Album } from './album/Album';
import { Phone } from './phone-list/Phone';
import { Song } from './song/Song';

const ENDPOINT = 'http://localhost/tpeAngular/api/albums';
const ENDPOINT2 = 'http://localhost/apiSpotify/api/albums';
const SONGENDPOINT = 'http://localhost/apiSpotify/api/albums';


@Injectable({
  providedIn: 'root'
})
export class TechDataService {

  constructor(private http: HttpClient) { }

  public getAll(): Observable<Phone[]> {

    return this.http.get<Phone[]>(ENDPOINT)
              .pipe(
                tap( (phones: Phone[]) => phones.forEach(phone => phone.quantity = 0))
              );
  }

  public getAll2(): Observable<Album[]> {
    return this.http.get<Album[]>(ENDPOINT2)
  }

  public getAllSongs(): Observable<Song[]> {
    return this.http.get<Song[]>(ENDPOINT2)
  }
}
