import { Component, Input } from '@angular/core';
import { Song } from '../song/Song';

@Component({
  selector: 'app-input-rate',
  templateUrl: './input-rate.component.html',
  styleUrls: ['./input-rate.component.scss']
})
export class InputRateComponent {

  constructor(){
  }

  @Input()
  quantity!: number;

  upRate(song: Song): void{
    if (song.rate < 5) {
      song.rate++;
    }
  }

  downRate(song: Song): void{
    if (song.rate > 0) {
      song.rate--;
    }
  }

  changeRate(event: { key: any; }, song: Song): void{
    console.log(event.key);
  }
}
