import { Component } from '@angular/core';
import { CartService } from '../cart.service';
import { TechDataService } from '../tech-data.service';
import { Album } from './Album';

@Component({
  selector: 'app-album',
  templateUrl: './album.component.html',
  styleUrls: ['./album.component.scss']
})
export class AlbumComponent {
  constructor(private cart: CartService, private dataService: TechDataService) {
  }

  ngOnInit(): void {
    this.dataService.getAll().subscribe(albums => this.albums = albums);
  }

  albums: Album[] = [];

  upQuantity(album: Album): void {
    if (phone.quantity >= 0 && phone.quantity < phone.stock) {
      phone.quantity++;
    }
  }

  downQuantity(phone: Phone): void {
    if (phone.quantity > 0) {
      phone.quantity--;
    }
  }

  onChangeQuantity(event: any, phone: Phone): void {
    const pattern = /[0-9\+\-\ ]/;
    let inputChar = String.fromCharCode(event.key);

    if (!pattern.test(inputChar)) {
      event.preventDefault();
    }
  }

  maxReached(event:any) {
    alert("The limit is: " + event);
  }

  addToCart(phone: Phone): void {
    this.cart.addToCart(phone);
    phone.stock -= phone.quantity;
    phone.quantity = 0;
  }
}
