import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';
import { Song } from './song/Song';

@Injectable({
  providedIn: 'root'
})

export class RatesService {

  private _rateList: Song[] = [];

  constructor() { }



  shoppingCart: BehaviorSubject<Song[]> = new BehaviorSubject(this._rateList);

  rateSong(song: Song) {
    let item = this._rateList.find((v1) => (v1.name == song.name));
    if (item) {
      item.rate += song.rate;
    }
    else {
      this._rateList.push({... song});
    }

    console.log(this._rateList);
    this.shoppingCart.next(this._rateList);
  }
}
