import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';
import { Phone } from './phone-list/Phone';
import { Album } from './album/Album';
import { Song } from './song/Song';

@Injectable({
  providedIn: 'root'
})
export class CartService {

  constructor() { }

  private _shoppingCart: Phone[] = [];
  shoppingCart: BehaviorSubject<Phone[]> = new BehaviorSubject(this._shoppingCart);

  private _shoppingCart2: Album[] = [];
  shoppingCart2: BehaviorSubject<Album[]> = new BehaviorSubject(this._shoppingCart2);

  private _favouriteSongs: Song[] = [];
  favouriteSongs: BehaviorSubject<Song[]> = new BehaviorSubject(this._favouriteSongs);

  addToCart(phone: Phone) {
    let item = this._shoppingCart.find((v1) => (v1.model == phone.model && v1.brand == phone.brand));
    if (phone.quantity > 0) {
      if (item) {
        item.quantity += phone.quantity;
      }
      else {
        this._shoppingCart.push({... phone});
      }
    }
  }

  addToCart2(album: Album) {
    this._shoppingCart2.push({... album});
    this.shoppingCart2.next(this._shoppingCart2);
  }

  addFavouriteSong(song: Song) {
    this._shoppingCart2.push({... song});
    this.shoppingCart2.next(this._favouriteSongs);
  }
}
