import { Component, Input } from '@angular/core';
import { Phone } from '../phone-list/Phone';

@Component({
  selector: 'app-items-quantity',
  templateUrl: './items-quantity.component.html',
  styleUrls: ['./items-quantity.component.scss']
})
export class ItemsQuantityComponent {

  @Input()
  quantity!: number;

  @Input()
  stock!: number;

  upQuantity(): void {
    if (this.quantity >= 0 && this.quantity < this.stock) {
      this.quantity++;
    }
  }

  downQuantity(): void {
    if (this.quantity > 0) {
      this.quantity--;
    }
  }

  onChangeQuantity(event: any): void {
    const pattern = /[0-9\+\-\ ]/;
    let inputChar = String.fromCharCode(event.key);

    if (!pattern.test(inputChar)) {
      event.preventDefault();
    }
  }
}
