import { Injectable } from '@angular/core';
import { Phone } from './phone-list/Phone';

@Injectable({
  providedIn: 'root'
})
export class CartService {

  constructor() { }

  shoppingCart: Phone[] = [];

  addToCart(phone: Phone, quantity) {
    this.shoppingCart.push(phone);
  }


}
