import { Component, OnInit } from '@angular/core';
import { CartService } from '../cart.service';
import { TechDataService } from '../tech-data.service';
import { Song } from './Song';

@Component({
  selector: 'app-song',
  templateUrl: './song.component.html',
  styleUrls: ['./song.component.scss']
})

export class SongComponent implements OnInit {
  constructor(private cart: CartService, private dataService: TechDataService) {
  }

  ngOnInit(): void {
    this.dataService.getAllSongs().subscribe(songs => this.songs = songs);
  }

  songs: Song[] = [];

  addToCart(song: Song): void {
    this.cart.addFavouriteSong(song);
  }
}
