import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Phone } from './phone-list/Phone';

const ENDPOINT = 'http://localhost/tpeAngular/api/albums';

@Injectable({
  providedIn: 'root'
})
export class TechDataService {

  constructor(private http: HttpClient) { }

  public getAll(): Observable<Phone[]> {

    return this.http.get<Phone[]>(ENDPOINT)
              .pipe(
                tap( (phones: Phone[]) => console.log(phones));
              );
  }
}
